var target;
export default target = (selector, targetIndex = 0) => {
    if (typeof(targetIndex) == "object") {
        if (targetIndex.length === 1) {
            ti = parseInt(targetIndex[0]);
            return document.querySelectorAll(selector)[ti];
        }
        else if (targetIndex.length === 2) {
           var list = [], elems = document.querySelectorAll(selector);
        if (targetIndex[1] == -1) {
            targetIndex[1] = Number.POSITIVE_INFINITY;
        }
        for (let i = targetIndex[0]; i <= targetIndex[1]; i++) {
            if (elems[i]) {
                list.push(elems[i]);
            }
            else {break}
        }
        return list
        }
        else if (targetIndex.length === 3) {
            var list = [], elems = document.querySelectorAll(selector);
            if (targetIndex[1] == -1) {
                targetIndex[1] = Number.POSITIVE_INFINITY;
            }
            for (let i = targetIndex[0]; i <= targetIndex[1]; i+=targetIndex[2]) {
                if (elems[i]) {
                    list.push(elems[i]);
                }
                else {break}
            }
            return list
        }
        
    }
    else {
        if (targetIndex === "all") {
            return document.querySelectorAll(selector);
        }
        else {
            let ti = parseInt(targetIndex);
            return document.querySelectorAll(selector)[ti];
        }
    }
}
